import{_ as e,o as i,c as l,a}from"./app.b1a3c5cb.js";const P=JSON.parse('{"title":"\u8BA1\u7B97\u673A\u662F\u600E\u6837\u8DD1\u8D77\u6765\u7684","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u8BA1\u7B97\u673A\u7684\u4E09\u5927\u7279\u6027","slug":"\u8BA1\u7B97\u673A\u7684\u4E09\u5927\u7279\u6027","link":"#\u8BA1\u7B97\u673A\u7684\u4E09\u5927\u7279\u6027","children":[{"level":3,"title":"\u8BA1\u7B97\u673A\u7684\u57FA\u672C\u786C\u4EF6\u7EC4\u6210","slug":"\u8BA1\u7B97\u673A\u7684\u57FA\u672C\u786C\u4EF6\u7EC4\u6210","link":"#\u8BA1\u7B97\u673A\u7684\u57FA\u672C\u786C\u4EF6\u7EC4\u6210","children":[]},{"level":3,"title":"\u51AF\xB7\u8BFA\u4F9D\u66FC\u4F53\u7CFB\u7ED3\u6784","slug":"\u51AF\xB7\u8BFA\u4F9D\u66FC\u4F53\u7CFB\u7ED3\u6784","link":"#\u51AF\xB7\u8BFA\u4F9D\u66FC\u4F53\u7CFB\u7ED3\u6784","children":[]}]},{"level":2,"title":"\u6027\u80FD","slug":"\u6027\u80FD","link":"#\u6027\u80FD","children":[{"level":3,"title":"\u4EC0\u4E48\u662F\u6027\u80FD","slug":"\u4EC0\u4E48\u662F\u6027\u80FD","link":"#\u4EC0\u4E48\u662F\u6027\u80FD","children":[]},{"level":3,"title":"\u8BA1\u7B97\u673A\u7684\u8BA1\u65F6\u5355\u4F4D:CPU \u65F6\u949F","slug":"\u8BA1\u7B97\u673A\u7684\u8BA1\u65F6\u5355\u4F4D-cpu-\u65F6\u949F","link":"#\u8BA1\u7B97\u673A\u7684\u8BA1\u65F6\u5355\u4F4D-cpu-\u65F6\u949F","children":[]},{"level":3,"title":"\u529F\u8017","slug":"\u529F\u8017","link":"#\u529F\u8017","children":[]},{"level":3,"title":"\u963F\u59C6\u8FBE\u5C14\u5B9A\u5F8B","slug":"\u963F\u59C6\u8FBE\u5C14\u5B9A\u5F8B","link":"#\u963F\u59C6\u8FBE\u5C14\u5B9A\u5F8B","children":[]}]},{"level":2,"title":"\u8BA1\u7B97\u673A\u6307\u4EE4","slug":"\u8BA1\u7B97\u673A\u6307\u4EE4","link":"#\u8BA1\u7B97\u673A\u6307\u4EE4","children":[{"level":3,"title":"\u4EE3\u7801\u8F6C\u6362\u6210\u673A\u5668\u7801","slug":"\u4EE3\u7801\u8F6C\u6362\u6210\u673A\u5668\u7801","link":"#\u4EE3\u7801\u8F6C\u6362\u6210\u673A\u5668\u7801","children":[]},{"level":3,"title":"\u89E3\u6790\u6307\u4EE4\u548C\u673A\u5668\u7801","slug":"\u89E3\u6790\u6307\u4EE4\u548C\u673A\u5668\u7801","link":"#\u89E3\u6790\u6307\u4EE4\u548C\u673A\u5668\u7801","children":[]}]}],"relativePath":"basics/computer/howComputerRun.md","lastUpdated":1668872608000}'),t={name:"basics/computer/howComputerRun.md"},r=a('<h1 id="\u8BA1\u7B97\u673A\u662F\u600E\u6837\u8DD1\u8D77\u6765\u7684" tabindex="-1">\u8BA1\u7B97\u673A\u662F\u600E\u6837\u8DD1\u8D77\u6765\u7684 <a class="header-anchor" href="#\u8BA1\u7B97\u673A\u662F\u600E\u6837\u8DD1\u8D77\u6765\u7684" aria-hidden="true">#</a></h1><h2 id="\u8BA1\u7B97\u673A\u7684\u4E09\u5927\u7279\u6027" tabindex="-1">\u8BA1\u7B97\u673A\u7684\u4E09\u5927\u7279\u6027 <a class="header-anchor" href="#\u8BA1\u7B97\u673A\u7684\u4E09\u5927\u7279\u6027" aria-hidden="true">#</a></h2><ul><li>\u8BA1\u7B97\u673A\u662F\u6267\u884C\u8F93\u5165\u3001\u8FD0\u7B97\u3001\u8F93\u51FA\u7684\u673A\u5668</li><li>\u7A0B\u5E8F\u662F\u6307\u4EE4\u548C\u6570\u636E\u7684\u96C6\u5408</li><li>\u8BA1\u7B97\u673A\u7684\u5904\u7406\u65B9\u5F0F\u4E0E\u56FA\u6709\u601D\u7EF4\u4E0D\u540C</li></ul><p>IC(\u96C6\u6210\u7535\u8DEF)</p><h3 id="\u8BA1\u7B97\u673A\u7684\u57FA\u672C\u786C\u4EF6\u7EC4\u6210" tabindex="-1">\u8BA1\u7B97\u673A\u7684\u57FA\u672C\u786C\u4EF6\u7EC4\u6210 <a class="header-anchor" href="#\u8BA1\u7B97\u673A\u7684\u57FA\u672C\u786C\u4EF6\u7EC4\u6210" aria-hidden="true">#</a></h3><p>\u8BA1\u7B97\u673A\u4E09\u5927\u4EF6\uFF1ACPU\u3001\u5185\u5B58\u548C\u4E3B\u677F</p><blockquote><p>CPU(Central Processing Unit)\u4E2D\u592E\u5904\u7406\u5668</p></blockquote><p>\u8BA1\u7B97\u673A\u7684\u6240\u6709\u201C\u8BA1\u7B97\u201D\u90FD\u662F\u7531 CPU \u6765\u8FDB\u884C\u7684\uFF0CCPU \u4E5F\u662F\u6574\u53F0\u8BA1\u7B97\u673A\u4E2D\u9020\u4EF7\u6700\u6602\u8D35\u7684\u90E8\u5206\u4E4B\u4E00\u3002CPU\u662F\u4E00\u4E2A\u8D85\u7EA7\u7CBE\u7EC6\u7684\u5370\u5237\u7535\u8DEF\u7248\u3002</p><blockquote><p>\u5185\u5B58(Memory)</p></blockquote><p>\u64B0\u5199\u7684\u7A0B\u5E8F\u3001\u6253\u5F00\u7684\u6D4F\u89C8\u5668\u3001\u8FD0\u884C\u7684\u6E38 \u620F\uFF0C\u90FD\u8981\u52A0\u8F7D\u5230\u5185\u5B58\u91CC\u624D\u80FD\u8FD0\u884C\u3002\u7A0B\u5E8F\u8BFB\u53D6\u7684\u6570\u636E\u3001\u8BA1\u7B97\u5F97\u5230\u7684\u7ED3\u679C\uFF0C\u4E5F\u90FD\u8981\u653E\u5728\u5185\u5B58\u91CC\u3002\u5185\u5B58\u8D8A\u5927\uFF0C\u80FD\u52A0\u8F7D\u7684\u4E1C\u897F\u81EA\u7136\u4E5F\u5C31\u8D8A\u591A\u3002</p><blockquote><p>\u4E3B\u677F(Motherboard)</p></blockquote><p>\u4E3B\u677F\u7684\u82AF\u7247\u7EC4(Chipset)\u548C\u603B\u7EBF(Bus)\u89E3\u51B3\u4E86 <code>CPU</code> \u548C <code>\u5185\u5B58</code> \u4E4B\u95F4\u5982\u4F55\u901A\u4FE1\u7684\u95EE\u9898\u3002\u82AF\u7247\u7EC4\u63A7\u5236\u4E86\u6570\u636E\u4F20\u8F93\u7684\u6D41\u8F6C,\u63A7\u5236\u6570\u636E\u4ECE\u54EA\u91CC\u5230\u54EA\u91CC\u3002\u603B\u7EBF\u5219\u662F\u9AD8\u901F\u516C\u8DEF\u3002\u603B\u7EBF\u901F\u5EA6\uFF0C\u51B3\u5B9A\u4E86\u6570\u636E\u80FD\u4F20\u8F93\u5F97\u591A\u5FEB\u3002</p><blockquote><p>IO\u8BBE\u5907\uFF08Input/Output\uFF09\u8BBE\u5907</p></blockquote><p>\u7528\u4E8E\u548C\u8BA1\u7B97\u673A\u8FDB\u884C\u4EA4\u4E92\u3002\u4F5C\u4E3A\u5916\u90E8 I/O \u8BBE\u5907\uFF0C\u5B83\u4EEC\u662F\u901A\u8FC7\u4E3B\u677F\u4E0A\u7684 \u5357\u6865(SouthBridge)\u82AF\u7247\u7EC4\uFF0C\u6765\u63A7\u5236\u548C CPU \u4E4B\u95F4\u7684\u901A\u4FE1\u7684\u3002</p><ul><li>\u5357\u6865\u5728\u4E3B\u677F\u4E0A\u7684\u4F4D\u7F6E\u5357\u8FB9\uFF0C\u53E6\u4E00\u65B9\u9762\uFF0C\u5B83\u7684\u4F5C\u7528\u5C31\u662F\u4F5C\u4E3A<code>\u6865</code>\uFF0C\u6765\u8FDE\u63A5\u9F20\u6807\u3001\u952E\u76D8\u4EE5\u53CA\u786C\u76D8\u8FD9\u4E9B\u5916\u90E8\u8BBE\u5907\u548C CPU \u4E4B\u95F4\u7684\u901A\u4FE1\u3002</li><li>\u4EE5\u524D\u7684\u4E3B\u677F\u4E0A\u901A\u5E38\u4E5F\u6709\u201C\u5317\u6865\u201D\u82AF\u7247\uFF0C\u7528\u6765\u4F5C \u4E3A\u201C\u6865\u201D\uFF0C\u8FDE\u63A5 CPU \u548C\u5185\u5B58\u3001\u663E\u5361\u4E4B\u95F4\u7684\u901A\u4FE1\u3002\u4E0D\u8FC7\uFF0C\u968F\u7740\u65F6\u95F4\u7684\u53D8\u8FC1\uFF0C\u73B0\u5728\u7684\u4E3B\u677F\u4E0A \u7684\u201C\u5317\u6865\u201D\u82AF\u7247\u7684\u5DE5\u4F5C\uFF0C\u5DF2\u7ECF\u88AB\u79FB\u5230\u4E86 CPU \u7684\u5185\u90E8\uFF0C\u6240\u4EE5\u4F60\u5728\u4E3B\u677F\u4E0A\uFF0C\u5DF2\u7ECF\u770B\u4E0D\u5230\u5317\u6865\u82AF \u7247\u4E86\u3002</li></ul><blockquote><p>\u663E\u5361(Graphics Card)</p></blockquote><p>\u901A\u5E38\u4E3B\u677F\u90FD\u5E26\u4E86\u5185\u7F6E\u7684\u663E\u5361\uFF0C\u5982\u679C\u9700\u8981\u73A9\u6E38\u620F\u548C\u505A\u5927\u89C4\u6A21\u6E32\u67D3\uFF0C\u5219\u9700\u8981\u72EC\u7ACB\u663E\u5361\uFF0C\u63D2\u5728\u4E3B\u677F\u4E0A\u3002\u663E\u5361\u4E4B\u6240\u4EE5\u7279\u6B8A\uFF0C\u662F\u56E0\u4E3A\u663E\u5361\u91CC\u6709\u9664\u4E86 CPU \u4E4B\u5916\u7684\u53E6\u4E00\u4E2A\u201C\u5904 \u7406\u5668\u201D\uFF0C\u4E5F\u5C31\u662FGPU(Graphics Processing Unit\uFF0C\u56FE\u5F62\u5904\u7406\u5668)\uFF0CGPU \u4E00\u6837\u53EF\u4EE5\u505A\u5404 \u79CD\u201C\u8BA1\u7B97\u201D\u7684\u5DE5\u4F5C\u3002</p><h3 id="\u51AF\xB7\u8BFA\u4F9D\u66FC\u4F53\u7CFB\u7ED3\u6784" tabindex="-1">\u51AF\xB7\u8BFA\u4F9D\u66FC\u4F53\u7CFB\u7ED3\u6784 <a class="header-anchor" href="#\u51AF\xB7\u8BFA\u4F9D\u66FC\u4F53\u7CFB\u7ED3\u6784" aria-hidden="true">#</a></h3><p>\u51AF\xB7\u8BFA\u4F9D\u66FC\u4F53\u7CFB\u7ED3\u6784\u4E5F\u53EB\u5B58\u50A8\u7A0B\u5E8F\u8BA1\u7B97\u673A\uFF0C\u62E5\u6709\u8FD0\u7B97\u5668\u3001\u63A7\u5236\u5668\u3001\u5B58\u50A8\u5668\u3001\u8F93\u5165\u8BBE\u5907\u548C\u8F93\u51FA\u8BBE\u5907\u8FD9\u4E94\u5927\u57FA\u672C\u7EC4\u4EF6\u3002</p><h4 id="\u624B\u673A\u7684\u786C\u4EF6\u7EC4\u6210" tabindex="-1">\u624B\u673A\u7684\u786C\u4EF6\u7EC4\u6210 <a class="header-anchor" href="#\u624B\u673A\u7684\u786C\u4EF6\u7EC4\u6210" aria-hidden="true">#</a></h4><p>\u6211\u4EEC\u624B\u673A\u91CC\u53EA\u6709 SD \u5361(Secure Digital Memory Card)\u8FD9\u6837\u7C7B\u4F3C\u786C\u76D8\u529F\u80FD\u7684\u5B58\u50A8\u5361\u63D2\u69FD\uFF0C\u5E76\u6CA1\u6709\u5185\u5B58\u63D2\u69FD\u3001CPU\u63D2\u69FD\u8FD9\u4E9B\u4E1C\u897F\u3002\u56E0\u4E3A\u624B\u673A\u5C3A\u5BF8\u7684\u539F\u56E0\uFF0C\u624B\u673A\u5236\u9020\u5546\u4EEC\u628A CPU\u3001\u5185\u5B58\u3001\u7F51\u7EDC\u901A\u4FE1\uFF0C\u4E43\u81F3\u6444\u50CF\u5934\u82AF\u7247\uFF0C\u90FD\u5C01\u88C5\u5230\u4E00\u4E2A\u82AF\u7247\uFF0C\u7136\u540E\u518D\u5D4C\u5165\u5230\u624B\u673A\u4E3B\u677F\u4E0A\u3002\u8FD9\u79CD\u65B9\u5F0F\u53EBSoC\uFF0C\u4E5F\u5C31\u662F System on a Chip(\u7CFB\u7EDF\u82AF\u7247)\u3002</p><h4 id="\u53EF\u7F16\u7A0B-\u8BA1\u7B97\u673A-\u5B58\u50A8-\u8BA1\u7B97\u673A" tabindex="-1">\u201C\u53EF\u7F16\u7A0B\u201D\u8BA1\u7B97\u673A\uFF0C\u201C\u5B58\u50A8\u201D\u8BA1\u7B97\u673A <a class="header-anchor" href="#\u53EF\u7F16\u7A0B-\u8BA1\u7B97\u673A-\u5B58\u50A8-\u8BA1\u7B97\u673A" aria-hidden="true">#</a></h4><blockquote><p>\u53EF\u7F16\u7A0B</p></blockquote><p>\u8BA1\u7B97\u673A\u662F\u7531\u5404\u79CD\u95E8\u7535\u8DEF\u7EC4\u5408\u800C\u6210\u7684\uFF0C\u7136\u540E\u901A\u8FC7\u7EC4\u88C5\u51FA\u4E00\u4E2A\u56FA\u5B9A\u7684\u7535\u8DEF\u7248\uFF0C\u6765\u5B8C\u6210\u4E00\u4E2A\u7279\u5B9A\u7684\u8BA1\u7B97\u7A0B\u5E8F\u3002\u4E00\u65E6\u9700\u8981\u4FEE\u6539\u529F\u80FD\uFF0C\u5C31\u8981\u91CD\u65B0\u7EC4\u88C5\u7535\u8DEF\u3002\u8FD9\u6837\u5C31\u4E0D\u662F\u53EF\u7F16\u7A0B\u7684\u8BA1\u7B97\u673A\u4E86\u3002\u8001\u5F0F\u8BA1\u7B97\u5668\u7684\u672C\u8D28\u662F\u4E00\u4E2A\u4E0D\u53EF\u7F16\u7A0B\u7684\u8BA1\u7B97\u673A\u3002\u56E0\u4E3A\u7535\u8DEF\u677F\u8BBE\u597D\u4E86\u52A0\u51CF\u4E58\u9664\uFF0C\u505A\u4E0D\u4E86\u4EFB\u4F55\u8BA1\u7B97\u903B\u8F91\u56FA\u5B9A\u4E4B\u5916\u7684\u4E8B\u60C5\u3002</p><blockquote><p>\u5B58\u50A8</p></blockquote><p>\u7A0B\u5E8F\u672C\u8EAB\u662F\u5B58\u50A8\u5728\u8BA1\u7B97\u673A\u7684\u5185\u5B58\u91CC\uFF0C\u53EF\u4EE5\u901A\u8FC7\u52A0\u8F7D\u4E0D\u540C\u7684\u7A0B\u5E8F\u6765\u89E3\u51B3\u4E0D\u540C\u7684\u95EE\u9898\u3002\u5982\u679C\u4E0D\u53EF\u5B58\u50A8\uFF0C\u53EA\u80FD\u4F7F\u7528\u56FA\u5B9A\u7684\u7A0B\u5E8F\u3002</p><h4 id="first-draft-\u7B2C\u4E00\u4EFD\u8349\u6848" tabindex="-1">First Draft(\u7B2C\u4E00\u4EFD\u8349\u6848) <a class="header-anchor" href="#first-draft-\u7B2C\u4E00\u4EFD\u8349\u6848" aria-hidden="true">#</a></h4><p>\u9996\u5148\u662F\u4E00\u4E2A\u5305\u542B\u7B97\u672F\u903B\u8F91\u5355\u5143(Arithmetic Logic Unit\uFF0CALU)\uFF0C\u548C\u5904\u7406\u5668\u5BC4\u5B58\u5668 (Processor Register)\u7684\u5904\u7406\u5668\u5355\u5143(Processing Unit)\uFF0C\u7528\u6765\u5B8C\u6210\u5404\u79CD\u7B97\u672F\u548C\u903B\u8F91\u8FD0\u7B97\uFF0C\u5B83\u80FD\u591F\u5B8C\u6210\u5404\u79CD\u6570\u636E\u7684\u5904\u7406\u6216\u8005\u8BA1\u7B97\u5DE5\u4F5C\uFF0C\u53EF\u4EE5\u79F0\u5176\u4E3A\u6570\u636E\u901A\u8DEF (Datapath)\u6216\u8005\u8FD0\u7B97\u5668\u3002</p><p>\u7136\u540E\u662F\u4E00\u4E2A\u5305\u542B\u6307\u4EE4\u5BC4\u5B58\u5668(Instruction Reigster)\u548C\u7A0B\u5E8F\u8BA1\u6570\u5668(Program Counter) \u7684\u63A7\u5236\u5668\u5355\u5143(Control Unit/CU)\uFF0C\u7528\u6765\u63A7\u5236\u7A0B\u5E8F\u7684\u6D41\u7A0B\u3002</p><p>\u5728\u73B0\u5728\u7684\u8BA1\u7B97\u673A\u91CC\uFF0C\u4E0A\u9762\u7684\u7B97\u672F\u903B\u8F91\u5355\u5143\u548C\u8FD9\u91CC\u7684\u63A7\u5236\u5668\u5355\u5143\uFF0C\u5171\u540C\u7EC4\u6210\u4E86\u6211\u4EEC\u8BF4\u7684 <code>CPU</code> \u3002</p><p>\u7528\u6765\u5B58\u50A8\u6570\u636E(Data)\u548C\u6307\u4EE4(Instruction)\u7684\u5185\u5B58\u3002\u4EE5\u53CA\u66F4\u5927\u5BB9\u91CF\u7684\u5916\u90E8\u5B58\u50A8\uFF0C\u5728\u8FC7\u53BB\uFF0C\u53EF\u80FD\u662F\u78C1\u5E26\u3001\u78C1\u9F13\u8FD9\u6837\u7684\u8BBE\u5907\uFF0C\u73B0\u5728\u901A\u5E38\u5C31\u662F\u786C\u76D8\u3002</p><p>\u7528\u6765\u548C\u8BA1\u7B97\u673A\u8FDB\u884C\u4EA4\u4E92\u7684\u5404\u79CD\u8F93\u5165\u548C\u8F93\u51FA\u8BBE\u5907\uFF0C\u4EE5\u53CA\u5BF9\u5E94\u7684\u8F93\u5165\u548C\u8F93\u51FA\u673A\u5236\u3002</p><h2 id="\u6027\u80FD" tabindex="-1">\u6027\u80FD <a class="header-anchor" href="#\u6027\u80FD" aria-hidden="true">#</a></h2><p>\u6027\u80FD\u662F\u8861\u91CF\u8FD0\u884C\u6548\u7387\u7684\u5173\u952E\u8BCD\u3002</p><h3 id="\u4EC0\u4E48\u662F\u6027\u80FD" tabindex="-1">\u4EC0\u4E48\u662F\u6027\u80FD <a class="header-anchor" href="#\u4EC0\u4E48\u662F\u6027\u80FD" aria-hidden="true">#</a></h3><p><code>\u6027\u80FD\u76D1\u6D4B\u5DE5\u5177 NewRelic</code></p><ul><li><p>\u7B2C\u4E00\u4E2A\u662F\u54CD\u5E94\u65F6\u95F4(Response time)\u6216\u8005\u53EB\u6267\u884C\u65F6\u95F4(Execution time)\u3002\u60F3\u8981\u63D0\u5347\u54CD\u5E94\u65F6\u95F4\u8FD9\u4E2A\u6027\u80FD\u6307\u6807\uFF0C\u4F60\u53EF\u4EE5\u7406\u89E3\u4E3A\u8BA9\u8BA1\u7B97\u673A\u201C\u8DD1\u5F97\u66F4\u5FEB\u201D\u3002\u54CD\u5E94\u65F6\u95F4\u4EE3\u8868\u4E86\u6BCF\u4E2A\u5916\u90E8\u7684 Web \u8BF7\u6C42\u7684\u6267\u884C\u65F6\u95F4</p></li><li><p>\u7B2C\u4E8C\u4E2A\u662F\u541E\u5410\u91CF(Throughput)\u6216\u8005\u5E26\u5BBD(Bandwidth)\uFF1A\u6307\u6211\u4EEC\u5728\u4E00\u5B9A\u7684\u65F6\u95F4\u8303\u56F4\u5185\uFF0C\u5230\u5E95\u80FD\u5904\u7406\u591A\u5C11\u4E8B\u60C5\u3002</p></li></ul><p>\u63D0\u5347\u6027\u80FD\uFF1A</p><ul><li>\u63D0\u5347\u541E\u5410\u91CF\uFF1A\u589E\u52A0\u6838\u5FC3\u6570\uFF0C\u5728\u5355\u4F4D\u65F6\u95F4 \u5185\u5C31\u53EF\u4EE5\u5904\u7406\u66F4\u591A\u6570\u636E\uFF0C\u541E\u5410\u7387\u63D0\u5347</li><li>\u591A\u52A0\u4E00\u4E9B\u673A\u5668\uFF0C\u5806\u786C\u4EF6</li><li>\u63D0\u5347\u54CD\u5E94\u65F6\u95F4\uFF1A\u4ECEcpu\u5165\u624B</li></ul><h3 id="\u8BA1\u7B97\u673A\u7684\u8BA1\u65F6\u5355\u4F4D-cpu-\u65F6\u949F" tabindex="-1">\u8BA1\u7B97\u673A\u7684\u8BA1\u65F6\u5355\u4F4D:CPU \u65F6\u949F <a class="header-anchor" href="#\u8BA1\u7B97\u673A\u7684\u8BA1\u65F6\u5355\u4F4D-cpu-\u65F6\u949F" aria-hidden="true">#</a></h3><p>\u901A\u8FC7 <code>Linux</code> \u4E0B\u6709\u4E00\u4E2A\u53EB <code>time</code> \u7684\u547D\u4EE4\uFF0C\u53EF\u4EE5\u5E2E\u6211\u4EEC\u7EDF\u8BA1\u51FA\u6765\uFF0C\u540C\u6837\u7684 <code>Wall Clock Time</code>\uFF08\u8FD0\u884C\u7ED3\u675F\u7684\u65F6\u95F4\u51CF\u53BB\u7A0B\u5E8F\u5F00\u59CB\u8FD0\u884C\u7684\u65F6\u95F4\uFF09\u4E0B\uFF0C\u7A0B\u5E8F\u5B9E\u9645\u5728 <code>CPU</code> \u4E0A\u5230\u5E95\u82B1\u4E86\u591A\u5C11\u65F6\u95F4\u3002</p><p>\u8FD0\u884C\u4E00\u4E0B <code>time</code> \u547D\u4EE4\u3002\u4ED6\u4F1A\u8FD4\u56DE\u4E09\u4E2A\u503C\uFF0C</p><ul><li>\u7B2C\u4E00\u4E2A\u662F<code>real time</code>,\u4E5F\u5C31\u662F\u8FD0\u884C\u7A0B\u5E8F\u6574\u4E2A\u8FC7\u7A0B\u4E2D\u6D41\u901D\u6389\u7684\u65F6\u95F4\u3002</li><li>\u7B2C\u4E8C\u4E2A\u662F<code>user time</code>,\u4E5F\u5C31\u662F <code>CPU</code> \u5728\u8FD0\u884C\u4F60\u7684\u7A0B\u5E8F\uFF0C\u5728\u7528\u6237\u6001\u8FD0\u884C\u6307\u4EE4\u7684\u65F6\u95F4\u3002</li><li>\u7B2C\u4E09\u4E2A\u662F<code>sys time</code>,\u662F <code>CPU</code> \u5728\u8FD0\u884C\u4F60\u7684\u7A0B\u5E8F\uFF0C\u5728\u64CD\u4F5C\u7CFB\u7EDF\u5185\u6838\u91CC\u8FD0\u884C\u6307\u4EE4\u7684\u65F6\u95F4\u3002</li></ul><p>\u7A0B\u5E8F\u5B9E\u9645\u82B1\u8D39\u7684 CPU \u6267\u884C\u65F6\u95F4(CPU Time)\uFF0C\u5C31\u662F user time \u52A0\u4E0A sys time\u3002</p><p>\u5373\u4F7F\u6211\u4EEC\u5DF2\u7ECF\u62FF\u5230\u4E86 CPU \u65F6\u95F4\uFF0C\u6211\u4EEC\u4E5F\u4E0D\u4E00\u5B9A\u53EF\u4EE5\u76F4\u63A5\u201C\u6BD4\u8F83\u201D\u51FA\u4E24\u4E2A\u7A0B\u5E8F\u7684\u6027\u80FD\u5DEE\u5F02\u3002\u540C\u4E00\u53F0\u8BA1\u7B97\u673A\u4E0A\uFF0CCPU \u53EF\u80FD\u6EE1\u8F7D\u8FD0\u884C\u4E5F\u53EF\u80FD\u964D\u9891\u8FD0\u884C\uFF0C\u964D\u9891\u8FD0\u884C\u7684\u65F6\u5019\u81EA\u7136 \u82B1\u7684\u65F6\u95F4\u4F1A\u591A\u4E00\u4E9B\u3002\u6027\u80FD\u6307\u6807\u8FD8\u4F1A\u53D7\u5230\u4E3B\u677F\u3001\u5185\u5B58\u8FD9\u4E9B\u5176\u4ED6\u76F8\u5173\u786C\u4EF6\u7684\u5F71\u54CD</p><p>\u7A0B\u5E8F\u7684 CPU \u6267\u884C\u65F6\u95F4 =CPU \u65F6\u949F\u5468\u671F\u6570\xD7\u65F6\u949F\u5468\u671F\u65F6\u95F4</p><h3 id="\u529F\u8017" tabindex="-1">\u529F\u8017 <a class="header-anchor" href="#\u529F\u8017" aria-hidden="true">#</a></h3><p>CPU\u4E00\u822C\u88AB\u79F0\u4F5C\u8D85\u5927\u89C4\u6A21\u96C6\u6210\u7535\u8DEF\uFF0C\u8FD9\u4E9B\u7535\u8DEF\uFF0C\u5B9E\u9645\u4E0A\u90FD\u662F\u4E00\u4E2A\u4E2A\u6676\u4F53\u7BA1\u7EC4\u5408\u800C\u6210\u7684\u3002\u8FD9\u4E9B\u7535\u8DEF\uFF0C\u5B9E\u9645\u4E0A\u90FD\u662F\u4E00\u4E2A\u4E2A\u6676\u4F53\u7BA1\u7EC4\u5408\u800C\u6210\u7684\u3002CPU \u5728\u8BA1\u7B97\uFF0C\u5176\u5B9E\u5C31\u662F\u8BA9\u6676\u4F53 \u7BA1\u91CC\u9762\u7684\u201C\u5F00\u5173\u201D\u4E0D\u65AD\u5730\u53BB\u201C\u6253\u5F00\u201D\u548C\u201C\u5173\u95ED\u201D\uFF0C\u6765\u7EC4\u5408\u5B8C\u6210\u5404\u79CD\u8FD0\u7B97\u548C\u529F\u80FD\u3002</p><p>\u63D0\u5347CPU\u901F\u5EA6\u53EF\u4EE5\u4ECE\u4EE5\u4E0B\u65B9\u5411\u5165\u624B</p><ul><li>\u589E\u52A0\u5BC6\u5EA6\uFF1A\u540C\u6837\u7684\u9762\u79EF\u91CC\u9762\uFF0C\u591A\u653E\u4E00\u4E9B\u6676\u4F53\u7BA1;</li><li>\u63D0\u5347\u4E3B\u9891\uFF1A\u8BA9\u6676\u4F53\u7BA1\u201C\u6253\u5F00\u201D\u548C\u201C\u5173\u95ED\u201D\u5F97\u66F4\u5FEB\u4E00\u70B9;</li></ul><p>\u529F\u8017 ~= 1/2 \xD7\u8D1F\u8F7D\u7535\u5BB9\xD7\u7535\u538B\u7684\u5E73\u65B9\xD7\u5F00\u5173\u9891\u7387\xD7\u6676\u4F53\u7BA1\u6570\u91CF</p><p>\u5236\u7A0B\u7684\u4F18\u5316\u548C\u7535\u538B\u7684\u4E0B\u964D\u53EF\u4EE5\u63D0\u5347cpu\u7684\u529F\u8017</p><h3 id="\u963F\u59C6\u8FBE\u5C14\u5B9A\u5F8B" tabindex="-1">\u963F\u59C6\u8FBE\u5C14\u5B9A\u5F8B <a class="header-anchor" href="#\u963F\u59C6\u8FBE\u5C14\u5B9A\u5F8B" aria-hidden="true">#</a></h3><p>CPU\u5E76\u884C,\u63D0\u5347\u541E\u5410\u91CF\u3002\u589E\u52A0CPU\u7684\u6838\u5FC3\u6570\u91CF\u3002</p><p>\u4F46\u662F\uFF0C\u5E76\u4E0D\u662F\u6240\u6709\u95EE\u9898\uFF0C\u90FD\u53EF\u4EE5\u901A\u8FC7\u5E76\u884C\u63D0\u9AD8\u6027\u80FD\u6765\u89E3\u51B3\u3002\u5982\u679C\u60F3\u8981\u4F7F\u7528\u8FD9\u79CD\u601D\u60F3\uFF0C\u9700\u8981\u6EE1\u8DB3\u8FD9\u6837\u51E0\u4E2A\u6761\u4EF6\u3002</p><ul><li>\u9700\u8981\u8FDB\u884C\u7684\u8BA1\u7B97\uFF0C\u672C\u8EAB\u53EF\u4EE5\u5206\u89E3\u6210\u51E0\u4E2A\u53EF\u4EE5\u5E76\u884C\u7684\u4EFB\u52A1</li><li>\u9700\u8981\u80FD\u591F\u5206\u89E3\u597D\u95EE\u9898\uFF0C\u5E76\u786E\u4FDD\u51E0\u4E2A\u7684\u7ED3\u679C\u80FD\u591F\u6C47\u603B\u5230\u4E00\u8D77</li><li>\u5728\u201C\u6C47\u603B\u201D\u8FD9\u4E2A\u9636\u6BB5\uFF0C\u662F\u6CA1\u6709\u529E\u6CD5\u5E76\u884C\u8FDB\u884C\u7684\uFF0C\u8FD8\u662F\u5F97\u987A\u5E8F\u6267\u884C\uFF0C\u4E00\u6B65\u4E00\u6B65\u6765\u3002</li></ul><p>\u963F\u59C6\u8FBE\u5C14\u5B9A\u5F8B\uFF1A\u4F18\u5316\u540E\u7684\u6267\u884C\u65F6\u95F4 = \u53D7\u4F18\u5316\u5F71\u54CD\u7684\u6267\u884C\u65F6\u95F4 / \u52A0\u901F\u500D\u6570 + \u4E0D\u53D7\u5F71\u54CD\u7684\u6267\u884C\u65F6\u95F4</p><h2 id="\u8BA1\u7B97\u673A\u6307\u4EE4" tabindex="-1">\u8BA1\u7B97\u673A\u6307\u4EE4 <a class="header-anchor" href="#\u8BA1\u7B97\u673A\u6307\u4EE4" aria-hidden="true">#</a></h2><p>\u786C\u4EF6\u7684\u89D2\u5EA6\u6765\u770B\uFF0CCPU \u5C31\u662F\u4E00\u4E2A\u8D85\u5927\u89C4\u6A21\u96C6\u6210\u7535\u8DEF\uFF0C\u901A\u8FC7\u7535\u8DEF\u5B9E\u73B0\u4E86\u52A0\u6CD5\u3001\u4E58\u6CD5\u4EE5\u53CA\u5404\u79CD\u5404\u6837\u7684\u5904\u7406\u903B\u8F91\u3002 \u8F6F\u4EF6\u89D2\u5EA6\u4E0A\u6765\u770B\uFF0CCPU \u5C31\u662F\u4E00\u4E2A\u6267\u884C\u5404\u79CD\u8BA1\u7B97\u673A\u6307\u4EE4(Instruction Code)\u7684\u903B\u8F91\u673A\u5668\uFF0C\u8BA1\u7B97\u673A\u6307\u4EE4\uFF0C\u5C31\u597D\u6BD4\u4E00\u95E8 CPU \u80FD\u591F\u542C\u5F97\u61C2\u7684\u8BED\u8A00\uFF0C\u6211\u4EEC\u4E5F\u53EF \u4EE5\u628A\u5B83\u53EB\u4F5C\u673A\u5668\u8BED\u8A00(Machine Language)\u3002 \u4E0D\u540C\u7684CPU\u67B6\u6784\u4E0D\u540C\uFF0C\u624B\u673A\u7528\u7684ARM\u7684 CPU\uFF0C\u7535\u8111\u7528\u7684Intel \u7684 CPU\u3002\u7C7B\u4F3C\u8FD9\u6837\u4E24\u79CD CPU \u5404\u81EA\u652F\u6301 \u7684\u8BED\u8A00\uFF0C\u5C31\u662F\u4E24\u7EC4\u4E0D\u540C\u7684\u8BA1\u7B97\u673A\u6307\u4EE4\u96C6\u3002</p><p>\u8BA1\u7B97\u673A\u5E73\u65F6\u90FD\u5B58\u50A8\u5728\u5B58\u50A8\u5668\u4E2D\uFF0C\u8FD9\u79CD\u7A0B\u5E8F\u50A8\u5B58\u5728\u5B58\u50A8\u5668\u4E2D\u90FD\u8BA1\u7B97\u673A\u5C31\u53EB\u505A\u5B58\u50A8\u7A0B\u5E8F\u8BA1\u7B97\u673A\u3002</p><h3 id="\u4EE3\u7801\u8F6C\u6362\u6210\u673A\u5668\u7801" tabindex="-1">\u4EE3\u7801\u8F6C\u6362\u6210\u673A\u5668\u7801 <a class="header-anchor" href="#\u4EE3\u7801\u8F6C\u6362\u6210\u673A\u5668\u7801" aria-hidden="true">#</a></h3><p>\u8981\u8BA9\u7A0B\u5E8F\u5728\u4E00\u4E2Alinux\u64CD\u4F5C\u7CFB\u7EDF\u8DD1\u8D77\u6765\uFF0C\u6211\u4EEC\u9700\u8981\u628A\u6574\u4E2A\u7A0B\u5E8F\u7FFB\u8BD1\u6210\u4E00\u4E2A\u6C47\u7F16\u8BED\u8A00(ASM\uFF0CAssembly Language)\u7684\u7A0B\u5E8F\u3002\u8FD9\u4E2A\u8FC7\u7A0B\u6211\u4EEC\u4E00\u822C\u53EB\u7F16\u8BD1(Compile)\u6210\u6C47\u7F16\u4EE3\u7801\u3002 \u9488\u5BF9\u6C47\u7F16\u4EE3\u7801\uFF0C\u53EF\u4EE5\u518D\u7528\u6C47\u7F16\u5668(Assembler)\u7FFB\u8BD1\u6210\u673A\u5668\u7801(Machine Code),\u8FD9\u4E9B\u673A\u5668\u7801\u7531\u201C0\u201D\u548C\u201C1\u201D\u7EC4\u6210\u7684\u673A\u5668\u8BED\u8A00\u8868\u793A\u3002\u8FD9\u4E00\u6761\u6761\u673A\u5668\u7801\uFF0C\u5C31\u662F\u4E00\u6761\u6761\u7684\u8BA1\u7B97\u673A\u6307\u4EE4\u3002</p><h3 id="\u89E3\u6790\u6307\u4EE4\u548C\u673A\u5668\u7801" tabindex="-1">\u89E3\u6790\u6307\u4EE4\u548C\u673A\u5668\u7801 <a class="header-anchor" href="#\u89E3\u6790\u6307\u4EE4\u548C\u673A\u5668\u7801" aria-hidden="true">#</a></h3><p>cup\u6307\u4EE4\uFF0C\u5E38\u7528\u7684\u82F1\u7279\u5C14\u6709 2000 \u6761\u5DE6\u53F3\u7684 CPU \u6307\u4EE4\uFF0C\u4ED6\u4EEC\u5206\u4E3A5\u5927\u7C7B\uFF1A</p><ul><li>\u7B97\u672F\u7C7B\u6307\u4EE4\uFF1A\u6211\u4EEC\u7684\u52A0\u51CF\u4E58\u9664\uFF0C\u5728 CPU \u5C42\u9762\uFF0C\u90FD\u4F1A\u53D8\u6210\u4E00\u6761\u6761\u7B97\u672F\u7C7B\u6307\u4EE4\u3002</li><li>\u6570\u636E\u4F20\u8F93\u7C7B\u6307\u4EE4\uFF1A\u7ED9\u53D8\u91CF\u8D4B\u503C\u3001\u5728\u5185\u5B58\u91CC\u8BFB\u5199\u6570\u636E\uFF0C\u7528\u7684\u90FD\u662F\u6570\u636E\u4F20\u8F93\u7C7B\u6307\u4EE4\u3002</li><li>\u903B\u8F91\u7C7B\u6307\u4EE4\uFF1A\u903B\u8F91\u4E0A\u7684\u4E0E\u6216\u975E\u3002</li><li>\u6761\u4EF6\u5206\u652F\u7C7B\u6307\u4EE4\uFF1A\u65E5\u5E38\u6211\u4EEC\u5199\u7684\u201Cif/else\u3002</li><li>\u65E0\u6761\u4EF6\u8DF3\u8F6C\u6307\u4EE4\uFF1A\u8C03\u7528\u51FD\u6570\u65F6\u8DF3\u8F6C\u3002</li></ul>',65),d=[r];function c(o,h,p,n,s,u){return i(),l("div",null,d)}const U=e(t,[["render",c]]);export{P as __pageData,U as default};
