import{_ as e,o as t,c as i,a as l}from"./app.9ef6ba01.js";var a="/assets/QpC7V4.bd2b7515.png",r="/assets/sPakpp.a36ae929.png";const L='{"title":"https","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u4E3A\u4EC0\u4E48\u6709https","slug":"\u4E3A\u4EC0\u4E48\u6709https"},{"level":2,"title":"\u52A0\u5BC6\u65B9\u6CD5","slug":"\u52A0\u5BC6\u65B9\u6CD5"},{"level":3,"title":"\u5171\u4EAB\u5BC6\u94A5\u52A0\u5BC6","slug":"\u5171\u4EAB\u5BC6\u94A5\u52A0\u5BC6"},{"level":3,"title":"\u4F7F\u7528\u4E24\u628A\u5BC6\u94A5\u7684\u516C\u5F00\u5BC6\u94A5\u52A0\u5BC6","slug":"\u4F7F\u7528\u4E24\u628A\u5BC6\u94A5\u7684\u516C\u5F00\u5BC6\u94A5\u52A0\u5BC6"},{"level":3,"title":"\u6DF7\u5408\u52A0\u5BC6\u65B9\u5F0F","slug":"\u6DF7\u5408\u52A0\u5BC6\u65B9\u5F0F"},{"level":3,"title":"\u8BC1\u660E\u516C\u5F00\u5BC6\u94A5\u6B63\u786E\u6027\u7684\u8BC1\u4E66","slug":"\u8BC1\u660E\u516C\u5F00\u5BC6\u94A5\u6B63\u786E\u6027\u7684\u8BC1\u4E66"},{"level":3,"title":"https\u7684\u901A\u4FE1\u673A\u5236","slug":"https\u7684\u901A\u4FE1\u673A\u5236"},{"level":3,"title":"HTTPS\u6BD4HTTP\u8981\u61622\u5230100\u500D","slug":"https\u6BD4http\u8981\u61622\u5230100\u500D"},{"level":2,"title":"HTTPS \u7684\u52A0\u5BC6\u6D41\u7A0B","slug":"https-\u7684\u52A0\u5BC6\u6D41\u7A0B"}],"relativePath":"basics/network/https.md","lastUpdated":1655193129000}',h={name:"basics/network/https.md"},s=l('<h1 id="https" tabindex="-1">https <a class="header-anchor" href="#https" aria-hidden="true">#</a></h1><p>SSL(Secure Socket Layer)\u5B89\u5168\u5957\u63A5\u5C42 TLS(Transport Layer Security)\u5B89\u5168\u5C42\u4F20\u8F93\u534F\u8BAE</p><h2 id="\u4E3A\u4EC0\u4E48\u6709https" tabindex="-1">\u4E3A\u4EC0\u4E48\u6709https <a class="header-anchor" href="#\u4E3A\u4EC0\u4E48\u6709https" aria-hidden="true">#</a></h2><p>http\u7684\u4E0D\u8DB3</p><ul><li>\u901A\u4FE1\u4F7F\u7528\u660E\u6587(\u4E0D\u52A0\u5BC6)\uFF0C\u5185\u5BB9\u53EF\u80FD\u4F1A\u88AB\u7A83\u542C</li><li>\u4E0D\u9A8C\u8BC1\u901A\u4FE1\u65B9\u7684\u8EAB\u4EFD\uFF0C\u56E0\u6B64\u6709\u53EF\u80FD\u906D\u9047\u4F2A\u88C5</li><li>\u65E0\u6CD5\u8BC1\u660E\u62A5\u6587\u7684\u5B8C\u6574\u6027\uFF0C\u6240\u4EE5\u6709\u53EF\u80FD\u5DF2\u906D\u7BE1\u6539</li></ul><p>https \u5C31\u662F\u52A0\u4E86\u4E00\u5C42ssl\u7684http\u3002</p><p>\u62A5\u6587\u9996\u90E8\u4E0D\u4F1A\u88AB\u52A0\u5BC6\uFF0C\u62A5\u6587\u4E3B\u4F53\u4F1A\u88AB\u52A0\u5BC6\u3002</p><h2 id="\u52A0\u5BC6\u65B9\u6CD5" tabindex="-1">\u52A0\u5BC6\u65B9\u6CD5 <a class="header-anchor" href="#\u52A0\u5BC6\u65B9\u6CD5" aria-hidden="true">#</a></h2><p>SSL \u91C7\u7528\u4E00\u79CD\u53EB\u505A\u516C\u5F00\u5BC6\u94A5\u52A0\u5BC6(Public-key cryptography)\u7684\u52A0\u5BC6\u5904\u7406\u65B9\u5F0F\u3002</p><p>\u8FD1\u4EE3\u7684\u52A0\u5BC6\u65B9\u6CD5\u4E2D\u52A0\u5BC6\u7B97\u6CD5\u662F\u516C\u5F00\u7684\uFF0C\u800C\u5BC6\u94A5\u662F\u4FDD\u5BC6\u7684\u3002\u901A\u8FC7\u8FD9\u79CD\u65B9\u5F0F\u5F97\u4EE5\u4FDD\u6301\u52A0\u5BC6\u65B9\u6CD5\u7684\u5B89\u5168\u6027\u3002\u52A0\u5BC6\u548C\u89E3\u5BC6\u90FD\u4F1A\u7528\u5230\u5BC6\u94A5\u3002\u6CA1\u6709\u5BC6\u94A5\u5C31\u65E0\u6CD5\u5BF9\u5BC6\u7801\u89E3\u5BC6\uFF0C\u53CD\u8FC7\u6765\u8BF4\uFF0C\u4EFB\u4F55\u4EBA\u53EA\u8981\u6301\u6709\u5BC6\u94A5\u5C31\u80FD\u89E3\u5BC6\u4E86\u3002\u5982\u679C\u5BC6\u94A5\u88AB\u653B\u51FB\u8005\u83B7\u5F97\uFF0C\u90A3\u52A0\u5BC6\u4E5F\u5C31\u5931\u53BB\u4E86\u610F\u4E49\u3002</p><h3 id="\u5171\u4EAB\u5BC6\u94A5\u52A0\u5BC6" tabindex="-1">\u5171\u4EAB\u5BC6\u94A5\u52A0\u5BC6 <a class="header-anchor" href="#\u5171\u4EAB\u5BC6\u94A5\u52A0\u5BC6" aria-hidden="true">#</a></h3><p>\u52A0\u5BC6\u548C\u89E3\u5BC6\u540C\u7528\u4E00\u4E2A\u5BC6\u94A5\u7684\u65B9\u5F0F\u79F0\u4E3A\u5171\u4EAB\u5BC6\u94A5\u52A0\u5BC6(Common key crypto system)\uFF0C\u4E5F\u88AB\u53EB\u505A\u5BF9\u79F0\u5BC6\u94A5\u52A0\u5BC6\u3002</p><p>\u4EE5\u5171\u4EAB\u5BC6\u94A5\u65B9\u5F0F\u52A0\u5BC6\u65F6\u5FC5\u987B\u5C06\u5BC6\u94A5\u4E5F\u53D1\u7ED9\u5BF9\u65B9\u3002\u5728\u4E92\u8054\u7F51\u4E0A\u8F6C\u53D1\u5BC6\u94A5\u65F6\uFF0C\u5982\u679C\u901A\u4FE1\u88AB\u76D1\u542C\u90A3\u4E48\u5BC6\u94A5\u5C31\u53EF\u4F1A\u843D\u5165\u653B\u51FB\u8005\u4E4B\u624B\uFF0C\u540C\u65F6\u4E5F\u5C31\u5931\u53BB\u4E86\u52A0\u5BC6\u7684\u610F\u4E49\u3002\u53E6\u5916\u8FD8\u5F97\u8BBE\u6CD5\u5B89\u5168\u5730\u4FDD\u7BA1\u63A5\u6536\u5230\u7684\u5BC6\u94A5\u3002</p><h3 id="\u4F7F\u7528\u4E24\u628A\u5BC6\u94A5\u7684\u516C\u5F00\u5BC6\u94A5\u52A0\u5BC6" tabindex="-1">\u4F7F\u7528\u4E24\u628A\u5BC6\u94A5\u7684\u516C\u5F00\u5BC6\u94A5\u52A0\u5BC6 <a class="header-anchor" href="#\u4F7F\u7528\u4E24\u628A\u5BC6\u94A5\u7684\u516C\u5F00\u5BC6\u94A5\u52A0\u5BC6" aria-hidden="true">#</a></h3><p>\u516C\u5F00\u5BC6\u94A5\u52A0\u5BC6\u4F7F\u7528\u4E00\u5BF9\u975E\u5BF9\u79F0\u7684\u5BC6\u94A5\u3002\u4E00\u628A\u53EB\u505A\u79C1\u6709\u5BC6\u94A5 (private key)\uFF0C\u53E6\u4E00\u628A\u53EB\u505A\u516C\u5F00\u5BC6\u94A5(public key)\u3002\u79C1\u6709\u5BC6\u94A5\u4E0D\u80FD\u8BA9\u5176\u4ED6\u4EFB\u4F55\u4EBA\u77E5\u9053\uFF0C\u800C\u516C\u5F00\u5BC6\u94A5\u5219\u53EF\u4EE5\u968F\u610F\u53D1\u5E03\uFF0C\u4EFB\u4F55\u4EBA\u90FD\u53EF\u4EE5\u83B7\u5F97\u3002\u4F7F\u7528\u516C\u5F00\u5BC6\u94A5\u52A0\u5BC6\u65B9\u5F0F\uFF0C\u53D1\u9001\u5BC6\u6587\u7684\u4E00\u65B9\u4F7F\u7528\u5BF9\u65B9\u516C\u5F00\u5BC6\u94A5\u8FDB\u884C\u52A0\u5BC6\u5904\u7406\uFF0C\u5BF9\u65B9\u6536\u5230\u88AB\u52A0\u5BC6\u7684\u4FE1\u606F\u540E\uFF0C\u518D\u4F7F\u7528\u81EA\u5DF1\u7684\u79C1\u6709\u5BC6\u94A5\u8FDB\u884C\u89E3\u5BC6\u3002</p><h3 id="\u6DF7\u5408\u52A0\u5BC6\u65B9\u5F0F" tabindex="-1">\u6DF7\u5408\u52A0\u5BC6\u65B9\u5F0F <a class="header-anchor" href="#\u6DF7\u5408\u52A0\u5BC6\u65B9\u5F0F" aria-hidden="true">#</a></h3><p>HTTPS \u91C7\u7528\u5171\u4EAB\u5BC6\u94A5\u52A0\u5BC6\u548C\u516C\u5F00\u5BC6\u94A5\u52A0\u5BC6\u4E24\u8005\u5E76\u7528\u7684\u6DF7\u5408\u52A0\u5BC6\u673A\u5236\u3002\u82E5\u5BC6\u94A5\u80FD\u591F\u5B9E\u73B0\u5B89\u5168\u4EA4\u6362\uFF0C\u90A3\u4E48\u6709\u53EF\u80FD\u4F1A\u8003\u8651\u4EC5\u4F7F\u7528\u516C\u5F00\u5BC6\u94A5\u52A0\u5BC6\u6765\u901A\u4FE1\u3002\u4F46\u662F\u516C\u5F00\u5BC6\u94A5\u52A0\u5BC6\u4E0E\u5171\u4EAB\u5BC6\u94A5\u52A0\u5BC6\u76F8\u6BD4\uFF0C\u5176\u5904\u7406\u901F\u5EA6\u8981\u6162\u3002</p><h3 id="\u8BC1\u660E\u516C\u5F00\u5BC6\u94A5\u6B63\u786E\u6027\u7684\u8BC1\u4E66" tabindex="-1">\u8BC1\u660E\u516C\u5F00\u5BC6\u94A5\u6B63\u786E\u6027\u7684\u8BC1\u4E66 <a class="header-anchor" href="#\u8BC1\u660E\u516C\u5F00\u5BC6\u94A5\u6B63\u786E\u6027\u7684\u8BC1\u4E66" aria-hidden="true">#</a></h3><p>\u516C\u5F00\u5BC6\u94A5\u52A0\u5BC6\u65B9\u5F0F\u65E0\u6CD5\u8BC1\u660E\u516C\u5F00\u5BC6\u94A5\u672C\u8EAB\u5C31\u662F\u8D27\u771F\u4EF7\u5B9E\u7684\u516C\u5F00\u5BC6\u94A5\uFF0C\u4F7F\u7528\u7531\u6570\u5B57\u8BC1\u4E66\u8BA4\u8BC1\u673A\u6784(CA\uFF0CCertificate Authority)\u548C\u5176\u76F8\u5173\u673A\u5173\u9881\u53D1\u7684\u516C\u5F00\u5BC6\u94A5\u8BC1\u4E66\u3002</p><p>\u516C\u94A5\u8BC1\u4E66\u4E5F\u53EF\u53EB\u505A\u6570\u5B57\u8BC1\u4E66\u6216\u76F4\u63A5\u79F0\u4E3A\u8BC1\u4E66\u3002\u63A5\u5230\u8BC1\u4E66\u7684\u5BA2\u6237\u7AEF\u53EF\u4F7F\u7528\u6570\u5B57\u8BC1\u4E66\u8BA4\u8BC1\u673A\u6784\u7684\u516C\u5F00\u5BC6\u94A5\uFF0C\u5BF9\u90A3\u5F20\u8BC1\u4E66\u4E0A\u7684\u6570\u5B57\u7B7E\u540D\u8FDB\u884C\u9A8C\u8BC1\uFF0C\u4E00\u65E6\u9A8C\u8BC1\u901A\u8FC7\uFF0C\u5BA2\u6237\u7AEF\u4FBF\u53EF\u660E\u786E\u4E24\u4EF6\u4E8B:</p><ul><li>\u8BA4\u8BC1\u670D\u52A1\u5668\u7684\u516C\u5F00\u5BC6\u94A5\u7684\u662F\u771F\u5B9E\u6709\u6548\u7684\u6570\u5B57\u8BC1\u4E66\u8BA4\u8BC1\u673A\u6784\u3002</li><li>\u670D\u52A1\u5668\u7684\u516C\u5F00\u5BC6\u94A5\u662F\u503C\u5F97\u4FE1\u8D56\u7684\u3002</li></ul><h3 id="https\u7684\u901A\u4FE1\u673A\u5236" tabindex="-1">https\u7684\u901A\u4FE1\u673A\u5236 <a class="header-anchor" href="#https\u7684\u901A\u4FE1\u673A\u5236" aria-hidden="true">#</a></h3><p><img src="'+a+'" alt="img"></p><ol><li>\u5BA2\u6237\u7AEF\u901A\u8FC7\u53D1\u9001 Client Hello \u62A5\u6587\u5F00\u59CB SSL \u901A\u4FE1\u3002\u62A5\u6587\u4E2D\u5305\u542B\u5BA2\u6237\u7AEF\u652F\u6301\u7684 SSL \u7684\u6307\u5B9A\u7248\u672C\u3001\u52A0\u5BC6\u7EC4\u4EF6(Cipher Suite)\u5217\u8868(\u6240\u4F7F\u7528\u7684\u52A0\u5BC6\u7B97\u6CD5\u53CA\u5BC6\u94A5\u957F\u5EA6\u7B49)\u3002</li><li>\u670D\u52A1\u5668\u53EF\u8FDB\u884C SSL \u901A\u4FE1\u65F6\uFF0C\u4F1A\u4EE5 Server Hello \u62A5\u6587\u4F5C\u4E3A\u5E94\u7B54\u3002\u548C\u5BA2\u6237\u7AEF\u4E00\u6837\uFF0C\u5728\u62A5\u6587\u4E2D\u5305\u542B SSL \u7248\u672C\u4EE5\u53CA\u52A0\u5BC6\u7EC4\u4EF6\u3002\u670D\u52A1\u5668\u7684\u52A0\u5BC6\u7EC4\u4EF6\u5185\u5BB9\u662F\u4ECE\u63A5\u6536\u5230\u7684\u5BA2\u6237\u7AEF\u52A0\u5BC6\u7EC4\u4EF6\u5185\u7B5B\u9009\u51FA\u6765\u7684\u3002</li><li>\u4E4B\u540E\u670D\u52A1\u5668\u53D1\u9001 Certificate \u62A5\u6587\u3002\u62A5\u6587\u4E2D\u5305\u542B\u516C\u5F00\u5BC6\u94A5\u8BC1\u4E66\u3002</li><li>\u6700\u540E\u670D\u52A1\u5668\u53D1\u9001 Server Hello Done \u62A5\u6587\u901A\u77E5\u5BA2\u6237\u7AEF\uFF0C\u6700\u521D\u9636\u6BB5\u7684 SSL \u63E1\u624B\u534F\u5546\u90E8\u5206\u7ED3\u675F\u3002</li><li>SSL \u7B2C\u4E00\u6B21\u63E1\u624B\u7ED3\u675F\u4E4B\u540E\uFF0C\u5BA2\u6237\u7AEF\u4EE5 Client Key Exchange \u62A5\u6587\u4F5C\u4E3A\u56DE\u5E94\u3002\u62A5\u6587\u4E2D\u5305\u542B\u901A\u4FE1\u52A0\u5BC6\u4E2D\u4F7F\u7528\u7684\u4E00\u79CD\u88AB\u79F0\u4E3A Pre-master secret \u7684\u968F\u673A\u5BC6\u7801\u4E32\u3002\u8BE5\u62A5\u6587\u5DF2\u7528\u6B65\u9AA4 3 \u4E2D\u7684\u516C\u5F00\u5BC6\u94A5\u8FDB\u884C\u52A0\u5BC6\u3002</li><li>\u63A5\u7740\u5BA2\u6237\u7AEF\u7EE7\u7EED\u53D1\u9001 Change Cipher Spec \u62A5\u6587\u3002\u8BE5\u62A5\u6587\u4F1A\u63D0\u793A\u670D\u52A1\u5668\uFF0C\u5728\u6B64\u62A5\u6587\u4E4B\u540E\u7684\u901A\u4FE1\u4F1A\u91C7\u7528 Pre-master secret \u5BC6\u94A5\u52A0\u5BC6\u3002</li><li>\u5BA2\u6237\u7AEF\u53D1\u9001 Finished \u62A5\u6587\u3002\u8BE5\u62A5\u6587\u5305\u542B\u8FDE\u63A5\u81F3\u4ECA\u5168\u90E8\u62A5\u6587\u7684\u6574\u4F53\u6821\u9A8C\u503C\u3002\u8FD9\u6B21\u63E1\u624B\u534F\u5546\u662F\u5426\u80FD\u591F\u6210\u529F\uFF0C\u8981\u4EE5\u670D\u52A1\u5668\u662F\u5426\u80FD\u591F\u6B63\u786E\u89E3\u5BC6\u8BE5\u62A5\u6587\u4F5C\u4E3A\u5224\u5B9A\u6807\u51C6\u3002</li><li>\u670D\u52A1\u5668\u540C\u6837\u53D1\u9001 Change Cipher Spec \u62A5\u6587\u3002</li><li>\u670D\u52A1\u5668\u540C\u6837\u53D1\u9001 Finished \u62A5\u6587\u3002</li><li>\u670D\u52A1\u5668\u548C\u5BA2\u6237\u7AEF\u7684 Finished \u62A5\u6587\u4EA4\u6362\u5B8C\u6BD5\u4E4B\u540E\uFF0CSSL \u8FDE\u63A5 \u5C31\u7B97\u5EFA\u7ACB\u5B8C\u6210\u3002\u5F53\u7136\uFF0C\u901A\u4FE1\u4F1A\u53D7\u5230 SSL \u7684\u4FDD\u62A4\u3002\u4ECE\u6B64\u5904\u5F00\u59CB\u8FDB\u884C\u5E94\u7528 \u5C42\u534F\u8BAE\u7684\u901A\u4FE1\uFF0C\u5373\u53D1\u9001 HTTP \u8BF7\u6C42\u3002</li><li>\u5E94\u7528\u5C42\u534F\u8BAE\u901A\u4FE1\uFF0C\u5373\u53D1\u9001 HTTP \u54CD\u5E94\u3002</li><li>\u6700\u540E\u7531\u5BA2\u6237\u7AEF\u65AD\u5F00\u8FDE\u63A5\u3002\u65AD\u5F00\u8FDE\u63A5\u65F6\uFF0C\u53D1\u9001 close_notify \u62A5 \u6587\u3002\u4E0A\u56FE\u505A\u4E86\u4E00\u4E9B\u7701\u7565\uFF0C\u8FD9\u6B65\u4E4B\u540E\u518D\u53D1\u9001 TCP FIN \u62A5\u6587\u6765\u5173\u95ED\u4E0E TCP \u7684\u901A\u4FE1\u3002</li></ol><p>HTTPS \u4F7F\u7528 SSL(Secure Socket Layer) \u548C TLS(Transport Layer Security)\u8FD9\u4E24\u4E2A\u534F\u8BAE\u3002 SSL \u6280\u672F\u6700\u521D\u662F\u7531\u6D4F\u89C8\u5668\u5F00\u53D1\u5546\u7F51\u666F\u901A\u4FE1\u516C\u53F8\u7387\u5148\u5021\u5BFC\u7684\uFF0C\u5F00\u53D1\u8FC7 SSL3.0 \u4E4B\u524D\u7684\u7248\u672C\u3002\u76EE\u524D\u4E3B\u5BFC\u6743\u5DF2\u8F6C\u79FB\u5230 IETF(Internet Engineering Task Force\uFF0CInternet \u5DE5\u7A0B\u4EFB\u52A1\u7EC4)\u7684\u624B\u4E2D\u3002 IETF \u4EE5 SSL3.0 \u4E3A\u57FA\u51C6\uFF0C\u540E\u53C8\u5236\u5B9A\u4E86 TLS1.0\u3001TLS1.1 \u548C TLS1.2\u3002TSL \u662F\u4EE5 SSL \u4E3A\u539F\u578B\u5F00\u53D1\u7684\u534F\u8BAE\uFF0C\u6709\u65F6\u4F1A\u7EDF\u4E00\u79F0\u8BE5\u534F\u8BAE \u4E3A SSL\u3002\u5F53\u524D\u4E3B\u6D41\u7684\u7248\u672C\u662F SSL3.0 \u548C TLS1.0\u3002 \u7531\u4E8E SSL1.0 \u534F\u8BAE\u5728\u8BBE\u8BA1\u4E4B\u521D\u88AB\u53D1\u73B0\u51FA\u4E86\u95EE\u9898\uFF0C\u5C31\u6CA1\u6709\u5B9E\u9645\u6295\u5165 \u4F7F\u7528\u3002SSL2.0 \u4E5F\u88AB\u53D1\u73B0\u5B58\u5728\u95EE\u9898\uFF0C\u6240\u4EE5\u5F88\u591A\u6D4F\u89C8\u5668\u76F4\u63A5\u5E9F\u9664\u4E86\u8BE5\u534F\u8BAE\u7248\u672C\u3002</p><h3 id="https\u6BD4http\u8981\u61622\u5230100\u500D" tabindex="-1">HTTPS\u6BD4HTTP\u8981\u61622\u5230100\u500D <a class="header-anchor" href="#https\u6BD4http\u8981\u61622\u5230100\u500D" aria-hidden="true">#</a></h3><p><img src="'+r+'" alt="img"></p><ul><li>\u901A\u4FE1\u6162</li><li>\u6D88\u8017\u5927\u91CFCPU \u53CA\u5185\u5B58\u7B49\u8D44\u6E90\uFF0C\u5BFC\u81F4\u5904\u7406\u901F\u5EA6\u53D8\u6162\u3002</li><li>SSL \u5FC5\u987B\u8FDB\u884C\u52A0\u5BC6\u5904\u7406\u3002\u5728\u670D\u52A1\u5668\u548C\u5BA2\u6237\u7AEF\u90FD\u9700\u8981\u8FDB\u884C \u52A0\u5BC6\u548C\u89E3\u5BC6\u7684\u8FD0\u7B97\u5904\u7406\u3002\u56E0\u6B64\u4ECE\u7ED3\u679C\u4E0A\u8BB2\uFF0C\u6BD4\u8D77 HTTP \u4F1A\u66F4\u591A\u5730\u6D88\u8017\u670D\u52A1\u5668\u548C\u5BA2\u6237\u7AEF\u7684\u786C\u4EF6\u8D44\u6E90\uFF0C\u5BFC\u81F4\u8D1F\u8F7D\u589E\u5F3A\u3002</li></ul><h2 id="https-\u7684\u52A0\u5BC6\u6D41\u7A0B" tabindex="-1">HTTPS \u7684\u52A0\u5BC6\u6D41\u7A0B <a class="header-anchor" href="#https-\u7684\u52A0\u5BC6\u6D41\u7A0B" aria-hidden="true">#</a></h2><p>\u4E24\u4E2A\u6982\u5FF5:</p><ol><li>\u5BF9\u79F0\u52A0\u5BC6\uFF1A\u52A0\u5BC6\u548C\u89E3\u5BC6\u7684\u79D8\u94A5\u4F7F\u7528\u7684\u662F\u540C\u4E00\u4E2A.</li><li>\u975E\u5BF9\u79F0\u52A0\u5BC6\uFF1A\u4F7F\u7528\u4E24\u628A\u5BC6\u94A5\uFF0C\u4E00\u628A\u516C\u94A5\uFF08publickey\uFF09\u548C\u79C1\u94A5\uFF08privatekey\uFF09\u3002</li><li>Hash\u7B97\u6CD5\uFF08\u6458\u8981\u7B97\u6CD5\uFF09\uFF1A\u7528\u6237\u53EF\u4EE5\u901A\u8FC7hash\u7B97\u6CD5\u5BF9\u76EE\u6807\u4FE1\u606F\u751F\u6210\u4E00\u6BB5\u7279\u5B9A\u957F\u5EA6\u7684\u552F\u4E00hash\u503C\uFF0C\u5374\u4E0D\u80FD\u901A\u8FC7\u8FD9\u4E2Ahash\u503C\u91CD\u65B0\u83B7\u5F97\u76EE\u6807\u4FE1\u606F\u3002\uFF08jwt\uFF09</li></ol><p>\u6570\u636E\u901A\u8FC7\u5BF9\u79F0\u52A0\u5BC6\u7684\u65B9\u5F0F\u6765\u52A0\u5BC6\uFF0C\u5BF9\u79F0\u52A0\u5BC6\u7684\u5BC6\u94A5\u662F\u7531\u5BA2\u6237\u7AEF\u751F\u6210\u7684\u968F\u673A\u5B57\u7B26\u4E32\u6765\u5145\u5F53\uFF0C\u518D\u901A\u8FC7\u975E\u5BF9\u79F0\u52A0\u5BC6\u7684\u65B9\u5F0F\u52A0\u5BC6\u540E\u4F20\u9012\u5230\u670D\u52A1\u7AEF\u3002</p><p>https\u7684\u4E09\u6B21\u63E1\u624B\uFF1A</p><ul><li>\u670D\u52A1\u7AEF\u53D1\u9001\u8BC1\u4E66\u5230\u5BA2\u6237\u7AEF</li></ul><p>\u670D\u52A1\u7AEF\u5C06\u8BC1\u4E66\u53D1\u9001\u7ED9\u5BA2\u6237\u7AEF\uFF0C\u8BC1\u4E66\u5185\u5305\u542B\u670D\u52A1\u5668\u7AEF\u7684\u516C\u94A5\uFF0C\u8BC1\u4E66\u9881\u53D1\u673A\u6784\uFF0C\u8BC1\u4E66\u6709\u6548\u671F\uFF0C\u670D\u52A1\u7AEF\u57DF\u540D\u7B49\u4FE1\u606F\u3002</p><ul><li>\u5BA2\u6237\u7AEF\u5BF9\u8BC1\u4E66\u8FDB\u884C\u6821\u9A8C\u5E76\u4E14\u5411\u670D\u52A1\u7AEF\u53D1\u9001\u5BF9\u79F0\u5BC6\u94A5</li></ul><p>\u5BA2\u6237\u7AEF\u6536\u5230\u8BC1\u4E66\u540E\uFF0C\u4F1A\u9009\u62E9\u662F\u5426\u4FE1\u4EFB\u8BC1\u4E66\uFF0C\u8FD9\u91CC\u662F\u5426\u4FE1\u4EFB\u53EF\u4EE5\u6839\u636E\u57DF\u540D\uFF0C\u9881\u53D1\u673A\u6784\uFF0C\u6709\u6548\u671F\u7B49\u4FE1\u606F\u6765\u5224\u65AD\uFF0C\u5982\u679C\u8BC1\u4E66\u6821\u9A8C\u4E0D\u901A\u8FC7\uFF0C\u5C31\u7ED9\u4E88\u98CE\u9669\u63D0\u793A\u5E76\u65AD\u5F00\u8FDE\u63A5\uFF1B\u5982\u679C\u6821\u9A8C\u901A\u8FC7\uFF0C\u5C31\u751F\u6210\u4E00\u4E2A\u968F\u673A\u6570\uFF0C\u7528\u4F5C\u5BF9\u79F0\u52A0\u5BC6\u7684\u5BC6\u94A5\uFF0C\u5E76\u53D6\u51FA\u8BC1\u4E66\u4E2D\u7684\u516C\u94A5\uFF0C\u7528\u8BE5\u516C\u94A5\u5BF9\u968F\u673A\u6570\u8FDB\u884C\u52A0\u5BC6\uFF0C\u5C06\u52A0\u5BC6\u540E\u7684\u7ED3\u679C\u53D1\u9001\u5230\u670D\u52A1\u7AEF\u3002</p><ul><li>\u670D\u52A1\u7AEF\u7528\u6536\u5230\u7684\u5BF9\u79F0\u5BC6\u94A5\u52A0\u5BC6\u4E00\u6BB5\u63E1\u624B\u4FE1\u606F\uFF0C\u53D1\u9001\u5230\u5BA2\u6237\u7AEF</li></ul><p>\u670D\u52A1\u7AEF\u6536\u5230\u5BA2\u6237\u7AEF\u53D1\u6765\u7684\u5BC6\u94A5\uFF0C\u5148\u7528\u81EA\u5DF1\u7684\u79C1\u94A5\u89E3\u5BC6\uFF0C\u89E3\u5BC6\u6210\u529F\u540E\uFF0C\u7528\u8BE5\u5BF9\u79F0\u5BC6\u94A5\u53D1\u9001\u4E00\u6BB5\u63E1\u624B\u4FE1\u606F\u5230\u5BA2\u6237\u7AEF\u3002\u5BA2\u6237\u7AEF\u6536\u5230\u540E\uFF0C\u89E3\u5BC6\u6210\u529F\uFF0C\u81F3\u6B64\uFF0CHTTPS \u8FDE\u63A5\u6210\u529F\uFF0C\u540E\u7EED\u7684\u6570\u636E\u4F20\u8F93\u5C31\u4F1A\u4F7F\u7528\u8BE5\u5BF9\u79F0\u5BC6\u94A5\u6765\u8FDB\u884C\u52A0\u5BC6\u3002</p>',39),p=[s];function n(S,d,c,o,u,T){return t(),i("div",null,p)}var g=e(h,[["render",n]]);export{L as __pageData,g as default};
